        public void onDBRead_Robotlist()
        {
            string sql = "";
            bool bdataok = false;

            try
            {
                sql = string.Format("SELECT * FROM robot_t ");

                DataSet ds = new DataSet();
                MySqlDataAdapter da = new MySqlDataAdapter(sql, Data.Instance.G_DynaSqlCon);
                da.Fill(ds);

                int ncnt = ds.Tables[0].Rows.Count;
                if (ncnt > 0)
                {
                    for (int i = 0; i < ncnt; i++)
                    {
                        strrobotid = ds.Tables[0].Rows[i]["robot_id"].ToString();
                        strrobotname = ds.Tables[0].Rows[i]["robot_name"].ToString();
                        strrobotip = ds.Tables[0].Rows[i]["address"].ToString();
                        strrobotgroup = ds.Tables[0].Rows[i]["robot_group_id"].ToString();
                    }
                }
            }
            catch (MySqlException ex)
            {
                Console.Out.WriteLine("onDBRead_Robotlist err :={0}", ex.Message.ToString());
            }
            catch (Exception ex2)
            {
                Console.Out.WriteLine("onDBRead_Robotlist err :={0}", ex2.Message.ToString());
            }
        }

        private void gridView1_RowCellClick(object sender, DevExpress.XtraGrid.Views.Grid.RowCellClickEventArgs e)
