            strrobotname = gridView1.GetFocusedRowCellValue("task_name").ToString();
            strrobotid = gridView1.GetFocusedRowCellValue("task_id").ToString();
            strrobotip = gridView1.GetFocusedRowCellValue("mission_list").ToString();
            strrobotgroup = gridView1.GetFocusedRowCellValue("robot_list").ToString();
            actionIDX = gridView1.GetDataSourceRowIndex(e.RowHandle);
        }
    }
    
